#!/bin/bash
SIZES=(2000 5000 10000 20000 50000 100000)
for DATASET_SIZE in ${SIZES[@]}; do
  #DATASET_SIZE=50000 # number of products in the dataset. There is around 350 triples generated by product.
  PARALLELISM=16
  VERSION="2.2.0"
  if [ ! -f "../bsbm-tools/explore-${DATASET_SIZE}.ttl" ]; then
    cd ../bsbm-tools
    rm -rf td_data
    ./generate -fc -pc ${DATASET_SIZE} -s ttl -fn "explore-${DATASET_SIZE}" -ud -ufn "explore-update-${DATASET_SIZE}"
    mv td_data td_data-${DATASET_SIZE}
    cd ../blazegraph
  fi

  docker-compose stop
  docker-compose rm -f
  docker-compose up -d
  sleep 30

  curl -f -X POST -H 'Content-Type:text/turtle' -T "../bsbm-tools/explore-${DATASET_SIZE}.ttl" http://localhost:8889/blazegraph/sparql
  sleep 60

  curl -X POST "http://localhost:8889/blazegraph/sparql" \
    -H "Content-Type: application/x-www-form-urlencoded" \
    -H "Accept:application/sparql-results+json" \
    --data-urlencode 'format=json' \
    --data-urlencode 'query=SELECT (COUNT(*) as ?Triples) WHERE { ?s ?p ?o }' > "graph.${VERSION}.${DATASET_SIZE}.${PARALLELISM}.log"

  cd ../bsbm-tools
  rm -rf td_data
  cp td_data-${DATASET_SIZE} td_data
  ./testdriver -mt ${PARALLELISM} -ucf usecases/explore/sparql.txt -o "../blazegraph/bsbm.explore.blazegraph.${VERSION}.${DATASET_SIZE}.${PARALLELISM}.xml" http://localhost:8889/blazegraph/sparql
  #./testdriver -mt ${PARALLELISM} -ucf usecases/exploreAndUpdate/sparql.txt -o "../blazegraph/bsbm.exploreAndUpdate.blazegraph.${VERSION}.${DATASET_SIZE}.${PARALLELISM}.xml" http://localhost:8889/blazegraph/sparql -u http://localhost:8889/blazegraph/sparql -udataset "explore-update-${DATASET_SIZE}.nt"
  #./testdriver -mt ${PARALLELISM} -ucf usecases/businessIntelligence/sparql.txt -o "../blazegraph/bsbm.businessIntelligence.blazegraph.${VERSION}.${DATASET_SIZE}.${PARALLELISM}.xml" http://localhost:8889/blazegraph/sparql
  cd ../blazegraph

  docker stats --no-stream > "stats.${VERSION}.${DATASET_SIZE}.${PARALLELISM}.log"
  docker-compose logs > "docker.${VERSION}.${DATASET_SIZE}.${PARALLELISM}.log"
  docker-compose stop
  docker-compose rm -f
done